import './App.css';
import { Component } from 'react';
import Rows from './Rows.js';

class App extends Component {
  constructor(props){
    super(props);
    this.state = {
      //gridArray:[ [ "1", "2", "3"], [ "4", "5" , "6"], [ "6", "7" , "8"] ],
      //gridArray:[ [ true, false, false], [ false, true , false], [ true, false , true] , [ false, false , false]],
      gridArray: [],

      gridXLength: 0,
      gridYLength: 0,

    }
  }

  // When clicked on a Cell, then switch that cells status.
  onCellClick = (indexCol, indexRow) => {
    let gridTMP = this.state.gridArray;
    gridTMP[indexRow][indexCol] = !gridTMP[indexRow][indexCol];

    this.setState({gridArray: gridTMP});
  }

  // Function to call when the button is clicked.
  stepFunction(){

  }

  // Function to calculate the next generation of hte current grid,
  // according to the rules of the game of life.
  calculateNextGeneration(){
    let gridTMP = this.state.gridArray;
    let current3x3Grid;
    let currentCell;
    let index1 = 0;

    for (index1 = 0; index1 < 10; index1++){
      let index2;
      for (index2 = 0; index2 < 10; index2++){
        let index3
        for (index3 = 0; index3 < 10; index3++){
          let index4;
          for (index4 = 0; index4 < 10; index4++){

            // Check if the current cell is somewhere along the border of the Grid.
            if(index1 == 0 || index1 == this.state.gridYLength || index2 == 0 || index1 == this.state.gridXLength){
              let border = false;
              // TODO: Figure out which one of the aboe conditions is true. 
              if(index1 == 0){ // The current cell is at the top border.
                border = 1;
              }
              if(index2 == 0){ // The current cell is at the left border.
                border = 1;
              }
              if(index1 == this.state.gridYLength){ // The current cell is at the bottom border.
                border = 1;
              }
              if(index2 == this.state.gridXLength){ // The current cell is at the right border.
                border = 1;
              }
            }

            else{
              // TODO
            }
          }
        }
        current3x3Cell.push()
      }
    }

  }

  determineArrayDimensions(){
    let yLengthTMP = this.state.gridArray.length;
    let xLengthTMP = this.state.gridArray.reduce((x, y) => Math.max(x, y.length), 0);

    this.setState({gridXLength: xLengthTMP});
    this.setState({gridYLength: yLengthTMP});
  }

  fillGrid(){
    let gridTMP = this.state.gridArray;

    let index1;

    // Fill Grid with false.
    for (index1 = 0; index1 < 10; index1++){
      let index2;
      let row = [];
      for (index2 = 0; index2 < 10; index2++){
        row.push(false);
      }
      gridTMP.push(row)
    }

    this.setState({ gridArray: gridTMP});

  }

  componentDidMount(){
    this.fillGrid(); 
    this.determineArrayDimensions();
  }

  render(){
    return (

      <div className="App">
        <header className="App-header">
        </header>

        <body>

            <div className="GridDiv">
              { this.state.gridArray.map((Element, index)=> <Rows indexRow={index} value={Element} onCellClick={this.onCellClick}/>) }

              <button width="100px" height="100px" onClick={this.stepFunction()}>STEP</button>
            </div>

        </body>
      </div>
    );
  }

}

export default App;
