import './App.css';
import { Component } from 'react';
import Rows from './Rows.js';

class App extends Component {
  constructor(props){
    super(props);
    this.state = {
      //gridArray:[ [ "1", "2", "3"], [ "4", "5" , "6"], [ "6", "7" , "8"] ],
      //gridArray:[ [ true, false, false], [ false, true , false], [ true, false , true] , [ false, false , false]],
      gridArray: [],
      biggergridArray: [],

      gridXLength: 0,
      gridYLength: 0,

    }
  }

  // When clicked on a Cell, then switch that cells status.
  onCellClick = (indexCol, indexRow) => {
    let gridTMP = this.state.gridArray;
    gridTMP[indexRow][indexCol] = !gridTMP[indexRow][indexCol];

    this.setState({gridArray: gridTMP});
  }

  // Function to call when the button is clicked.
  stepFunction(){

  }

  // Function to calculate the next generation of hte current grid,
  // according to the rules of the game of life.
  calculateNextGeneration(){
    let gridTMP = this.state.gridArray;
    let biggerGrid = [];

    let index1;

    // Fill the bigger Grid with false.
    for (index1 = 0; index1 < this.state.gridYLength + 2; index1++){
      let index2;
      let row = [];
      for (index2 = 0; index2 < this.state.gridXLength + 2; index2++){
        /*
        // If the current cell is on the outermost loop of the bigger grid, fill it with false.
        if(index1 == 0 || index1 == this.state.gridYLength || index2 == 0 || index2 == this.state.gridXLength){
          row.push(false);
        }

        // If the current cell is NOT on the outermost loop of the bigger grid, fill it with the content the the grid.
        else{
          row.push(gridTMP[index1-1][index2-1]);
        }
        */
        row.push(false);

        console.log(index1 + " " + index2 + " " + this.state.gridYLength + " " + this.state.gridXLength);

      }
      biggerGrid.push(row)

    }

    this.setState({ biggergridArray: biggerGrid});


  }

  determineArrayDimensions(){
    let yLengthTMP = this.state.gridArray.length;
    let xLengthTMP = this.state.gridArray.reduce((x, y) => Math.max(x, y.length), 0);

    this.setState({gridXLength: xLengthTMP});
    this.setState({gridYLength: yLengthTMP});
  }

  fillGrid(){
    let gridTMP = this.state.gridArray;

    let index1;

    // Fill Grid with false.
    for (index1 = 0; index1 < 10; index1++){
      let index2;
      let row = [];
      for (index2 = 0; index2 < 10; index2++){
        row.push(false);
      }
      gridTMP.push(row)
    }

    this.setState({ gridArray: gridTMP});

  }

  componentDidMount(){
    this.fillGrid(); 
    this.determineArrayDimensions();
  }

  render(){
    this.calculateNextGeneration();

    return (

      <div className="App">
        <header className="App-header">
        </header>

        <body>

            <div className="GridDiv">
              { this.state.gridArray.map((Element, index)=> <Rows indexRow={index} value={Element} onCellClick={this.onCellClick}/>) }

              <button width="100px" height="100px" onClick={this.stepFunction()}>STEP</button>
            </div>

        </body>
      </div>
    );
  }

}

export default App;
